/*global describe it*/
import assert from 'assert';
import getStandardDeviation from '../src/getStandardDeviation.js';

const tests = [{
  values: [600, 470, 170, 430, 300],
  deviation: 147.32277488562318
},{
  values: [13,23,12,44,55],
  deviation: 17.21162397916013
}];


describe('getStandardDeviation()', function () {
  tests.forEach(function (test) {
    it(test.values, function () {
      assert.equal(getStandardDeviation(test.values), test.deviation);
    });
  });
});